import React, {useState,useEffect} from 'react'
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.css'
import {Link} from 'react-router-dom'

const Aluno =() =>{
    const [data,setData] = useState([])
    useEffect(() => {
    axios
    .get('https://appacademia-9ede4.firebaseio.com/Experiences.json')
    .then(res=>{
    setData(res.data)
    })
},[])
 const deleteSeries = id =>{
axios.delete('https://appacademia-9ede4.firebaseio.com/Experiences/'+ id)
.then(res =>{
const filtrado = data.filter(item=> item.id!==id)
setData(filtrado)
})
 }
const renderizalinha = record =>{

return(
  
     <tr key={record.id}>
      <th scope="row">{record.id}</th>
      <td>{record.nome}</td>
      <td><button className='btn btn-danger' onClick= {()=>deleteSeries(record.id)}>Remover</button></td>
      <Link to={'/series/' + record.id} className= 'btn btn-primary'>Info</Link>
    </tr>
)
}
if(data.length===0){
  return(
 <div className='container'>
   <h1> Séries </h1>
   <div><Link to='/series/novo'  className='btn btn-primary'>Nova Séries</Link> </div>
    <div className='alert alert-warning' role ='alert'>
     A simples warning alart-check it out!
  </div>
</div>
)
}
 

    return (
    <div className= 'container'>
    <h1> Séries </h1>
    <div><Link to='/series/novo'  className='btn btn-primary'>Nova Séries</Link> </div>
     <table className='table table-dark'>
  <thead>
    <tr>
      <th scope="col">Id</th>
      <th scope="col">Nome</th>
      <th scope="col">Açoes</th>
    </tr>
  </thead>
  <tbody>
     {data.map(renderizalinha)}
    </tbody>
     </table>
    </div>
    )
}
    export default Aluno
