import axios from 'axios'
//import 'bootstrap/dist/css/bootstrap.css'
import {Link} from 'react-router-dom'

const Generos =() =>{
    const [data,setData] = useState([])
    useEffect(() => {
    axios
    .get('https://appacademia-9ede4.firebaseio.com/Aluno.json')
    .then(res=>{
    setData(res.data.data)
    })
},[])
 const deleteGenero = id =>{
axios.delete('https://appacademia-9ede4.firebaseio.com/Aluno.json/'+ id)
.then(res =>{
const filtrado = data.filter(item=> item.id!==id)
setData(filtrado)
})
 }
const renderizalinha = record =>{

return(
  
     <tr key={record.id}>
      <th scope="row">{record.id}</th>
      <td>{record.name}</td>
      <td><button className='btn btn-danger' onClick= {()=>deleteGenero(record.id)}>Remover</button></td>
      <Link to={'/generos/' + record.id} className= 'btn btn-warning'>Editar</Link>
    </tr>
)
}
