import React, {useState, useRef} from 'react'
import {Redirect} from 'react-router-dom'
import constants from 'jest-haste-map/build/constants'
const minAno = 2019
const maxAno = 2022
const AdicionarMes = () => {
  const refAno = useRef()
  const refMes = useRef()
  const [redir,setRedir] = useState('')
  const anos = []
  const meses = []
  for(let i = minAno; i<=maxAno; i++){
   anos.push(i)

  }

  for (let i=1; i<=12; i++){
    meses.push(i)

  }

  const zeropad = num =>{
if(num <10){
  return '0'+num
}
 return num
 }
const verMes = () => {
  setRedir(refAno.current.value + '-' + refMes.current.value)
}

if(redir!==''){
  return  <Redirect to={'Movimentacoes/' + redir}/>
}
return (
  <React.Fragment>  
<h2>Adicionar mês</h2>
      <select ref={refAno}>
      {anos.map(ano => <option key={ano} value={ano}>{ano}</option>)}
      </select>
      <select>
       {meses.map(zeropad).map(mes => <option key={mes} value={mes}>{mes}</option>)}
       </select>      
     <button onClick={verMes}> Adicionar mês</button>
     </React.Fragment>
)
} 
export default AdicionarMes
